import React, { useState, useEffect } from 'react';
import Card from './Card';
import { getCards, createCard, deleteList } from '../services/api';

const List = ({ list, onDelete }) => {
  const [cards, setCards] = useState([]);
  const [showAddCard, setShowAddCard] = useState(false);
  const [newCardTitle, setNewCardTitle] = useState('');
  const [newCardDescription, setNewCardDescription] = useState('');
  const [showMenu, setShowMenu] = useState(false);

  useEffect(() => {
    fetchCards();
  }, [list.id]);

  const fetchCards = async () => {
    try {
      const data = await getCards(list.id);
      setCards(data);
    } catch (error) {
      console.error('Error fetching cards:', error);
    }
  };

  const handleCreateCard = async (e) => {
    e.preventDefault();
    if (newCardTitle.trim()) {
      try {
        const newCard = await createCard(list.id, newCardTitle, newCardDescription);
        setCards([...cards, newCard]);
        setNewCardTitle('');
        setNewCardDescription('');
        setShowAddCard(false);
      } catch (error) {
        console.error('Error creating card:', error);
      }
    }
  };

  const handleDeleteCard = (cardId) => {
    setCards(cards.filter(card => card.id !== cardId));
  };

  const handleDeleteList = async () => {
    if (window.confirm('Are you sure you want to delete this list and all its cards?')) {
      try {
        await deleteList(list.id);
        onDelete(list.id);
      } catch (error) {
        console.error('Error deleting list:', error);
      }
    }
  };

  return (
    <div className="flex-shrink-0 w-72 bg-gray-100 rounded-lg p-3">
      <div className="flex justify-between items-center mb-3">
        <h3 className="font-semibold text-gray-800">{list.title}</h3>
        <div className="relative">
          <button
            onClick={() => setShowMenu(!showMenu)}
            className="text-gray-500 hover:text-gray-700 p-1"
          >
            â‹¯
          </button>
          {showMenu && (
            <div className="absolute right-0 mt-2 w-32 bg-white rounded-md shadow-lg z-10">
              <button
                onClick={handleDeleteList}
                className="block w-full text-left px-4 py-2 text-sm text-red-600 hover:bg-gray-50"
              >
                Delete List
              </button>
            </div>
          )}
        </div>
      </div>

      <div className="space-y-2 mb-3 max-h-96 overflow-y-auto">
        {cards.map((card) => (
          <Card
            key={card.id}
            card={card}
            onDelete={() => handleDeleteCard(card.id)}
          />
        ))}
      </div>

      {/* Add Card Form */}
      {showAddCard ? (
        <form onSubmit={handleCreateCard} className="space-y-2">
          <textarea
            value={newCardTitle}
            onChange={(e) => setNewCardTitle(e.target.value)}
            placeholder="Enter a title for this card..."
            className="w-full p-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500 resize-none"
            rows="2"
            autoFocus
          />
          <textarea
            value={newCardDescription}
            onChange={(e) => setNewCardDescription(e.target.value)}
            placeholder="Enter description (optional)..."
            className="w-full p-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500 resize-none"
            rows="1"
          />
          <div className="flex space-x-2">
            <button
              type="submit"
              className="bg-blue-500 text-white px-3 py-1 rounded text-sm hover:bg-blue-600 transition-colors"
            >
              Add Card
            </button>
            <button
              type="button"
              onClick={() => {
                setShowAddCard(false);
                setNewCardTitle('');
                setNewCardDescription('');
              }}
              className="text-gray-600 px-3 py-1 rounded text-sm hover:text-gray-800 transition-colors"
            >
              Cancel
            </button>
          </div>
        </form>
      ) : (
        <button
          onClick={() => setShowAddCard(true)}
          className="w-full text-left text-gray-600 hover:text-gray-800 py-2 px-3 rounded hover:bg-gray-200 transition-colors"
        >
          + Add a card
        </button>
      )}
    </div>
  );
};

export default List;
