import React, { useState, useEffect } from 'react';
import { updateCard, deleteCard, getUsers, getCardLabels, createCardLabel, deleteCardLabel } from '../services/api';
import { MoreVertical, Edit3, Trash2, X, Calendar, Tag, Plus } from 'lucide-react';

const Card = ({ card, onDelete, onUpdate }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [title, setTitle] = useState(card.title);
  const [description, setDescription] = useState(card.description || '');
  const [status, setStatus] = useState(card.status || 'To Do');
  const [assignedTo, setAssignedTo] = useState(card.assigned_to || null);
  const [dueDate, setDueDate] = useState(card.due_date || '');
  const [showMenu, setShowMenu] = useState(false);
  const [users, setUsers] = useState([]);
  const [labels, setLabels] = useState([]);
  const [editErrors, setEditErrors] = useState({});
  const [showLabelForm, setShowLabelForm] = useState(false);
  const [newLabel, setNewLabel] = useState('');
  const [newLabelColor, setNewLabelColor] = useState('#3B82F6');

  useEffect(() => {
    fetchUsers();
    fetchLabels();
  }, [card.id]);

  const fetchUsers = async () => {
    try {
      const usersData = await getUsers();
      setUsers(usersData);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const fetchLabels = async () => {
    try {
      const labelsData = await getCardLabels(card.id);
      setLabels(labelsData);
    } catch (error) {
      console.error('Error fetching labels:', error);
    }
  };

  const validateCard = () => {
    const newErrors = {};
    if (!title.trim()) newErrors.title = 'Card title is required';
    else if (title.length > 100) newErrors.title = 'Card title must be less than 100 characters';

    if (description && description.length > 500)
      newErrors.description = 'Card description must be less than 500 characters';

    setEditErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSave = async () => {
    if (!validateCard()) return;

    try {
      await updateCard(card.id, title.trim(), description.trim(), status, assignedTo, null, null, dueDate);
      setIsEditing(false);
      setEditErrors({});
      if (onUpdate) onUpdate();
    } catch (error) {
      setEditErrors({ general: error.response?.data?.error || 'Error updating card' });
    }
  };

  const handleAddLabel = async () => {
    if (!newLabel.trim()) return;

    try {
      await createCardLabel(card.id, newLabel.trim(), newLabelColor);
      setNewLabel('');
      setNewLabelColor('#3B82F6');
      setShowLabelForm(false);
      fetchLabels();
    } catch (error) {
      console.error('Error adding label:', error);
    }
  };

  const handleDeleteLabel = async (labelId) => {
    try {
      await deleteCardLabel(labelId);
      fetchLabels();
    } catch (error) {
      console.error('Error deleting label:', error);
    }
  };

  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this card?')) {
      try {
        await deleteCard(card.id);
        onDelete(card.id);
      } catch (error) {
        console.error('Error deleting card:', error);
      }
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'To Do':
        return 'bg-gray-100 text-gray-800';
      case 'In Progress':
        return 'bg-yellow-100 text-yellow-800';
      case 'Done':
        return 'bg-green-100 text-green-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('vi-VN', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (isEditing) {
    return (
      <div className="bg-white rounded p-3 shadow-sm border border-gray-200">
        <textarea
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className={`w-full p-2 border rounded resize-none focus:outline-none focus:ring-2 ${
            editErrors.title
              ? 'border-red-500 focus:ring-red-500'
              : 'border-gray-300 focus:ring-blue-500'
          }`}
          rows="2"
        />
        {editErrors.title && <p className="text-red-600 text-sm">{editErrors.title}</p>}

        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Add description..."
          className="w-full p-2 mt-2 border border-gray-300 rounded resize-none focus:outline-none focus:ring-2 focus:ring-blue-500"
          rows="2"
        />

        <select
          value={status}
          onChange={(e) => setStatus(e.target.value)}
          className="w-full p-2 mt-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
        >
          <option value="To Do">To Do</option>
          <option value="In Progress">In Progress</option>
          <option value="Done">Done</option>
        </select>

        <select
          value={assignedTo || ''}
          onChange={(e) => setAssignedTo(e.target.value ? parseInt(e.target.value) : null)}
          className="w-full p-2 mt-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
        >
          <option value="">No one assigned</option>
          {users.map((user) => (
            <option key={user.id} value={user.id}>
              {user.username}
            </option>
          ))}
        </select>

        {editErrors.general && <p className="text-red-600 text-sm mt-2">{editErrors.general}</p>}

        <div className="flex justify-end space-x-2 mt-3">
          <button
            onClick={handleSave}
            className="bg-blue-600 text-white px-3 py-1 rounded text-sm hover:bg-blue-700"
          >
            Save
          </button>
          <button
            onClick={() => setIsEditing(false)}
            className="flex items-center gap-1 text-gray-600 hover:text-gray-800"
          >
            <X size={16} /> Cancel
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white rounded p-3 shadow-sm border border-gray-200 hover:shadow-md transition cursor-pointer group relative">
      <div onClick={() => setIsEditing(true)}>
        <div className="flex justify-between items-start mb-2">
          <h4 className="font-medium text-gray-800 text-sm flex-1">{card.title}</h4>
          <span
            className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(
              card.status || 'To Do'
            )}`}
          >
            {card.status || 'To Do'}
          </span>
        </div>
        {card.description && <p className="text-gray-600 text-xs mb-2">{card.description}</p>}
        <div className="flex justify-between items-center text-xs text-gray-400">
          <span>Created: {formatDate(card.created_at)}</span>
          {card.assigned_to && (
            <span className="bg-blue-100 text-blue-800 px-2 py-0.5 rounded">
              {users.find((u) => u.id === card.assigned_to)?.username || 'Unknown'}
            </span>
          )}
        </div>
      </div>

      {/* Menu button */}
      <button
        onClick={(e) => {
          e.stopPropagation();
          setShowMenu(!showMenu);
        }}
        className="absolute top-2 right-2 text-gray-400 hover:text-gray-600 opacity-0 group-hover:opacity-100 transition"
      >
        <MoreVertical size={18} />
      </button>

      {showMenu && (
        <div className="absolute top-8 right-2 w-32 bg-white rounded-md shadow-lg z-10 border border-gray-200">
          <button
            onClick={(e) => {
              e.stopPropagation();
              setIsEditing(true);
              setShowMenu(false);
            }}
            className="flex items-center gap-2 w-full px-4 py-2 text-sm text-gray-700 hover:bg-gray-50"
          >
            <Edit3 size={16} /> Edit
          </button>
          <button
            onClick={(e) => {
              e.stopPropagation();
              handleDelete();
              setShowMenu(false);
            }}
            className="flex items-center gap-2 w-full px-4 py-2 text-sm text-red-600 hover:bg-gray-50"
          >
            <Trash2 size={16} /> Delete
          </button>
        </div>
      )}
    </div>
  );
};

export default Card;
