import React, { useState, useEffect } from 'react';
import Card from './Card';
import ListExpanded from './ListExpanded';
import { getCards, createCard, deleteList } from '../services/api';

const List = ({ list, onDelete }) => {
  const [cards, setCards] = useState([]);
  const [showAddCard, setShowAddCard] = useState(false);
  const [newCardTitle, setNewCardTitle] = useState('');
  const [newCardDescription, setNewCardDescription] = useState('');
  const [showMenu, setShowMenu] = useState(false);
  const [showExpanded, setShowExpanded] = useState(false);

  useEffect(() => {
    fetchCards();
  }, [list.id]);

  const fetchCards = async () => {
    try {
      const data = await getCards(list.id);
      setCards(data);
    } catch (error) {
      console.error('Error fetching cards:', error);
    }
  };

  const [cardErrors, setCardErrors] = useState({});

  const validateCardForm = () => {
    const newErrors = {};
    
    if (!newCardTitle.trim()) {
      newErrors.title = 'Card title is required';
    } else if (newCardTitle.length > 100) {
      newErrors.title = 'Card title must be less than 100 characters';
    }
    
    if (newCardDescription && newCardDescription.length > 500) {
      newErrors.description = 'Card description must be less than 500 characters';
    }
    
    setCardErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleCreateCard = async (e) => {
    e.preventDefault();
    if (validateCardForm()) {
      try {
        const newCard = await createCard(list.id, newCardTitle.trim(), newCardDescription.trim(), 'To Do');
        setCards([...cards, newCard]);
        setNewCardTitle('');
        setNewCardDescription('');
        setCardErrors({});
        setShowAddCard(false);
      } catch (error) {
        console.error('Error creating card:', error);
        setCardErrors({ general: 'Error creating card: ' + (error.response?.data?.error || 'Unknown error') });
      }
    }
  };

  const handleCardInputChange = (field, value) => {
    if (field === 'title') {
      setNewCardTitle(value);
    } else if (field === 'description') {
      setNewCardDescription(value);
    }
    
    if (cardErrors[field]) {
      setCardErrors({ ...cardErrors, [field]: '' });
    }
  };

  const handleDeleteCard = (cardId) => {
    setCards(cards.filter(card => card.id !== cardId));
  };

  const handleCardUpdate = () => {
    // Refresh cards when a card is updated
    fetchCards();
  };

  const handleDeleteList = async () => {
    if (window.confirm('Are you sure you want to delete this list and all its cards?')) {
      try {
        await deleteList(list.id);
        onDelete(list.id);
      } catch (error) {
        console.error('Error deleting list:', error);
      }
    }
  };

  return (
    <div className="flex-shrink-0 w-72 bg-gray-100 rounded-lg p-3">
      <div className="flex justify-between items-center mb-3">
        <h3 className="font-semibold text-gray-800">{list.title}</h3>
        <div className="flex items-center space-x-2">
          <button
            onClick={() => setShowExpanded(true)}
            className="text-blue-600 hover:text-blue-800 text-xs px-2 py-1 rounded hover:bg-blue-50 transition-colors"
            title="Expand to 3-column view"
          >
            Expand
          </button>
          <div className="relative">
            <button
              onClick={() => setShowMenu(!showMenu)}
              className="text-gray-500 hover:text-gray-700 p-1"
            >
              ⋯
            </button>
            {showMenu && (
              <div className="absolute right-0 mt-2 w-32 bg-white rounded-md shadow-lg z-10">
                <button
                  onClick={handleDeleteList}
                  className="block w-full text-left px-4 py-2 text-sm text-red-600 hover:bg-gray-50"
                >
                  Delete List
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      <div className="space-y-2 mb-3 max-h-96 overflow-y-auto">
        {cards.map((card) => (
          <Card
            key={card.id}
            card={card}
            onDelete={() => handleDeleteCard(card.id)}
            onUpdate={handleCardUpdate}
          />
        ))}
      </div>

      {/* Add Card Form */}
      {showAddCard ? (
        <form onSubmit={handleCreateCard} className="space-y-2">
          <textarea
            value={newCardTitle}
            onChange={(e) => handleCardInputChange('title', e.target.value)}
            placeholder="Enter a title for this card..."
            className={`w-full p-2 border rounded focus:outline-none focus:ring-2 resize-none ${
              cardErrors.title 
                ? 'border-red-500 focus:ring-red-500' 
                : 'border-gray-300 focus:ring-blue-500'
            }`}
            rows="2"
            autoFocus
          />
          {cardErrors.title && (
            <p className="text-sm text-red-600">{cardErrors.title}</p>
          )}
          <textarea
            value={newCardDescription}
            onChange={(e) => handleCardInputChange('description', e.target.value)}
            placeholder="Enter description (optional)..."
            className={`w-full p-2 border rounded focus:outline-none focus:ring-2 resize-none ${
              cardErrors.description 
                ? 'border-red-500 focus:ring-red-500' 
                : 'border-gray-300 focus:ring-blue-500'
            }`}
            rows="1"
          />
          {cardErrors.description && (
            <p className="text-sm text-red-600">{cardErrors.description}</p>
          )}
          {cardErrors.general && (
            <p className="text-sm text-red-600">{cardErrors.general}</p>
          )}
          <div className="flex space-x-2">
            <button
              type="submit"
              className="bg-blue-500 text-white px-3 py-1 rounded text-sm hover:bg-blue-600 transition-colors"
            >
              Add Card
            </button>
            <button
              type="button"
              onClick={() => {
                setShowAddCard(false);
                setNewCardTitle('');
                setNewCardDescription('');
                setCardErrors({});
              }}
              className="text-gray-600 px-3 py-1 rounded text-sm hover:text-gray-800 transition-colors"
            >
              Cancel
            </button>
          </div>
        </form>
      ) : (
        <button
          onClick={() => setShowAddCard(true)}
          className="w-full text-left text-gray-600 hover:text-gray-800 py-2 px-3 rounded hover:bg-gray-200 transition-colors"
        >
          + Add a card
        </button>
      )}

      {/* Expanded View Modal */}
      {showExpanded && (
        <ListExpanded
          list={list}
          onClose={() => setShowExpanded(false)}
          onDelete={onDelete}
        />
      )}
    </div>
  );
};

export default List;
