import React, { useState, useEffect } from 'react';
import { updateCard, deleteCard, getUsers, getCardLabels, createCardLabel, deleteCardLabel, getCardAttachments, uploadCardAttachment, deleteCardAttachment } from '../services/api';
import { MoreVertical, Edit3, Trash2, X, Calendar, Tag, Plus, Paperclip, Download, File } from 'lucide-react';

const Card = ({ card, onDelete, onUpdate }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [title, setTitle] = useState(card.title);
  const [description, setDescription] = useState(card.description || '');
  const [status, setStatus] = useState(card.status || 'To Do');
  const [assignedTo, setAssignedTo] = useState(card.assigned_to || null);
  const [dueDate, setDueDate] = useState(card.due_date || '');
  const [showMenu, setShowMenu] = useState(false);
  const [users, setUsers] = useState([]);
  const [labels, setLabels] = useState([]);
  const [attachments, setAttachments] = useState([]);
  const [editErrors, setEditErrors] = useState({});
  const [showLabelForm, setShowLabelForm] = useState(false);
  const [showAttachmentForm, setShowAttachmentForm] = useState(false);
  const [newLabel, setNewLabel] = useState('');
  const [newLabelColor, setNewLabelColor] = useState('#3B82F6');
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    fetchUsers();
    fetchLabels();
    fetchAttachments();
  }, [card.id]);

  const fetchUsers = async () => {
    try {
      const usersData = await getUsers();
      setUsers(usersData);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const fetchLabels = async () => {
    try {
      const labelsData = await getCardLabels(card.id);
      setLabels(labelsData);
    } catch (error) {
      console.error('Error fetching labels:', error);
    }
  };

  const fetchAttachments = async () => {
    try {
      const attachmentsData = await getCardAttachments(card.id);
      setAttachments(attachmentsData);
    } catch (error) {
      console.error('Error fetching attachments:', error);
    }
  };

  const validateCard = () => {
    const newErrors = {};
    if (!title.trim()) newErrors.title = 'Card title is required';
    else if (title.length > 100) newErrors.title = 'Card title must be less than 100 characters';

    if (description && description.length > 500)
      newErrors.description = 'Card description must be less than 500 characters';

    setEditErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSave = async () => {
    if (!validateCard()) return;

    try {
      await updateCard(card.id, title.trim(), description.trim(), status, assignedTo, null, null, dueDate);
      setIsEditing(false);
      setEditErrors({});
      if (onUpdate) onUpdate();
    } catch (error) {
      setEditErrors({ general: error.response?.data?.error || 'Error updating card' });
    }
  };

  const handleAddLabel = async () => {
    if (!newLabel.trim()) return;

    try {
      await createCardLabel(card.id, newLabel.trim(), newLabelColor);
      setNewLabel('');
      setNewLabelColor('#3B82F6');
      setShowLabelForm(false);
      fetchLabels();
    } catch (error) {
      console.error('Error adding label:', error);
    }
  };

  const handleDeleteLabel = async (labelId) => {
    try {
      await deleteCardLabel(labelId);
      fetchLabels();
    } catch (error) {
      console.error('Error deleting label:', error);
    }
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setUploading(true);
    try {
      await uploadCardAttachment(card.id, file);
      fetchAttachments();
      setShowAttachmentForm(false);
    } catch (error) {
      console.error('Error uploading file:', error);
      alert('Error uploading file: ' + (error.response?.data?.error || 'Unknown error'));
    } finally {
      setUploading(false);
    }
  };

  const handleDeleteAttachment = async (attachmentId) => {
    if (window.confirm('Are you sure you want to delete this attachment?')) {
      try {
        await deleteCardAttachment(attachmentId);
        fetchAttachments();
      } catch (error) {
        console.error('Error deleting attachment:', error);
      }
    }
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const getFileIcon = (mimeType) => {
    if (mimeType.startsWith('image/')) return '🖼️';
    if (mimeType.startsWith('video/')) return '🎥';
    if (mimeType.startsWith('audio/')) return '🎵';
    if (mimeType.includes('pdf')) return '📄';
    if (mimeType.includes('word') || mimeType.includes('document')) return '📝';
    if (mimeType.includes('excel') || mimeType.includes('spreadsheet')) return '📊';
    if (mimeType.includes('zip') || mimeType.includes('rar')) return '📦';
    return '📎';
  };

  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this card?')) {
      try {
        await deleteCard(card.id);
        onDelete(card.id);
      } catch (error) {
        console.error('Error deleting card:', error);
      }
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'To Do':
        return 'bg-gray-100 text-gray-800';
      case 'In Progress':
        return 'bg-yellow-100 text-yellow-800';
      case 'Done':
        return 'bg-green-100 text-green-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('vi-VN', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const formatDueDate = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    const now = new Date();
    const diffTime = date - now;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    
    if (diffDays < 0) {
      return `Overdue by ${Math.abs(diffDays)} days`;
    } else if (diffDays === 0) {
      return 'Due today';
    } else if (diffDays === 1) {
      return 'Due tomorrow';
    } else {
      return `Due in ${diffDays} days`;
    }
  };

  const getDueDateColor = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    const now = new Date();
    const diffTime = date - now;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    
    if (diffDays < 0) return 'text-red-600 bg-red-100';
    if (diffDays === 0) return 'text-orange-600 bg-orange-100';
    if (diffDays <= 3) return 'text-yellow-600 bg-yellow-100';
    return 'text-gray-600 bg-gray-100';
  };

  const predefinedColors = [
    '#3B82F6', '#EF4444', '#10B981', '#F59E0B', 
    '#8B5CF6', '#EC4899', '#06B6D4', '#84CC16'
  ];

  if (isEditing) {
    return (
      <div className="bg-white rounded p-3 shadow-sm border border-gray-200">
        <textarea
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className={`w-full p-2 border rounded resize-none focus:outline-none focus:ring-2 ${
            editErrors.title
              ? 'border-red-500 focus:ring-red-500'
              : 'border-gray-300 focus:ring-blue-500'
          }`}
          rows="2"
        />
        {editErrors.title && <p className="text-red-600 text-sm">{editErrors.title}</p>}

        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Add description..."
          className="w-full p-2 mt-2 border border-gray-300 rounded resize-none focus:outline-none focus:ring-2 focus:ring-blue-500"
          rows="2"
        />

        <select
          value={status}
          onChange={(e) => setStatus(e.target.value)}
          className="w-full p-2 mt-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
        >
          <option value="To Do">To Do</option>
          <option value="In Progress">In Progress</option>
          <option value="Done">Done</option>
        </select>

        <select
          value={assignedTo || ''}
          onChange={(e) => setAssignedTo(e.target.value ? parseInt(e.target.value) : null)}
          className="w-full p-2 mt-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
        >
          <option value="">No one assigned</option>
          {users.map((user) => (
            <option key={user.id} value={user.id}>
              {user.username}
            </option>
          ))}
        </select>

        <input
          type="datetime-local"
          value={dueDate}
          onChange={(e) => setDueDate(e.target.value)}
          className="w-full p-2 mt-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
          placeholder="Due date"
        />

        {editErrors.general && <p className="text-red-600 text-sm mt-2">{editErrors.general}</p>}

        <div className="flex justify-end space-x-2 mt-3">
          <button
            onClick={handleSave}
            className="bg-blue-600 text-white px-3 py-1 rounded text-sm hover:bg-blue-700"
          >
            Save
          </button>
          <button
            onClick={() => setIsEditing(false)}
            className="flex items-center gap-1 text-gray-600 hover:text-gray-800"
          >
            <X size={16} /> Cancel
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white rounded p-3 shadow-sm border border-gray-200 hover:shadow-md transition cursor-pointer group relative">
      <div onClick={() => setIsEditing(true)}>
        <div className="flex justify-between items-start mb-2">
          <h4 className="font-medium text-gray-800 text-sm flex-1">{card.title}</h4>
          <span
            className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(
              card.status || 'To Do'
            )}`}
          >
            {card.status || 'To Do'}
          </span>
        </div>
        {card.description && <p className="text-gray-600 text-xs mb-2">{card.description}</p>}
        
        {/* Labels */}
        {labels.length > 0 && (
          <div className="flex flex-wrap gap-1 mb-2">
            {labels.map((label) => (
              <span
                key={label.id}
                className="text-xs px-2 py-0.5 rounded text-white"
                style={{ backgroundColor: label.color }}
              >
                {label.label}
              </span>
            ))}
          </div>
        )}
        
        {/* Due Date */}
        {card.due_date && (
          <div className={`text-xs px-2 py-1 rounded mb-2 ${getDueDateColor(card.due_date)}`}>
            <Calendar size={12} className="inline mr-1" />
            {formatDueDate(card.due_date)}
          </div>
        )}
        
        {/* Attachments */}
        {attachments.length > 0 && (
          <div className="mb-2">
            <div className="flex items-center text-xs text-gray-600 mb-1">
              <Paperclip size={12} className="mr-1" />
              Attachments ({attachments.length})
            </div>
            <div className="space-y-1">
              {attachments.slice(0, 3).map((attachment) => (
                <div key={attachment.id} className="flex items-center justify-between bg-gray-50 rounded px-2 py-1">
                  <div className="flex items-center flex-1 min-w-0">
                    <span className="mr-1">{getFileIcon(attachment.mime_type)}</span>
                    <span className="text-xs text-gray-700 truncate" title={attachment.original_name}>
                      {attachment.original_name}
                    </span>
                    <span className="text-xs text-gray-500 ml-1">
                      ({formatFileSize(attachment.file_size)})
                    </span>
                  </div>
                  <div className="flex items-center space-x-1">
                    <a
                      href={`http://localhost:5000/uploads/${attachment.filename}`}
                      download={attachment.original_name}
                      className="text-blue-600 hover:text-blue-800"
                      title="Download"
                    >
                      <Download size={12} />
                    </a>
                    <button
                      onClick={() => handleDeleteAttachment(attachment.id)}
                      className="text-red-600 hover:text-red-800"
                      title="Delete"
                    >
                      <X size={12} />
                    </button>
                  </div>
                </div>
              ))}
              {attachments.length > 3 && (
                <div className="text-xs text-gray-500 text-center">
                  +{attachments.length - 3} more files
                </div>
              )}
            </div>
          </div>
        )}
        
        <div className="flex justify-between items-center text-xs text-gray-400">
          <span>Created: {formatDate(card.created_at)}</span>
          {card.assigned_to && (
            <span className="bg-blue-100 text-blue-800 px-2 py-0.5 rounded">
              {users.find((u) => u.id === card.assigned_to)?.username || 'Unknown'}
            </span>
          )}
        </div>
      </div>

      {/* Menu button */}
      <button
        onClick={(e) => {
          e.stopPropagation();
          setShowMenu(!showMenu);
        }}
        className="absolute top-2 right-2 text-gray-400 hover:text-gray-600 opacity-0 group-hover:opacity-100 transition"
      >
        <MoreVertical size={18} />
      </button>

      {showMenu && (
        <div className="absolute top-8 right-2 w-40 bg-white rounded-md shadow-lg z-10 border border-gray-200">
          <button
            onClick={(e) => {
              e.stopPropagation();
              setIsEditing(true);
              setShowMenu(false);
            }}
            className="flex items-center gap-2 w-full px-4 py-2 text-sm text-gray-700 hover:bg-gray-50"
          >
            <Edit3 size={16} /> Edit
          </button>
          <button
            onClick={(e) => {
              e.stopPropagation();
              setShowLabelForm(true);
              setShowMenu(false);
            }}
            className="flex items-center gap-2 w-full px-4 py-2 text-sm text-gray-700 hover:bg-gray-50"
          >
            <Tag size={16} /> Add Label
          </button>
          <button
            onClick={(e) => {
              e.stopPropagation();
              setShowAttachmentForm(true);
              setShowMenu(false);
            }}
            className="flex items-center gap-2 w-full px-4 py-2 text-sm text-gray-700 hover:bg-gray-50"
          >
            <Paperclip size={16} /> Add File
          </button>
          <button
            onClick={(e) => {
              e.stopPropagation();
              handleDelete();
              setShowMenu(false);
            }}
            className="flex items-center gap-2 w-full px-4 py-2 text-sm text-red-600 hover:bg-gray-50"
          >
            <Trash2 size={16} /> Delete
          </button>
        </div>
      )}

      {/* Label Form */}
      {showLabelForm && (
        <div className="absolute top-8 right-2 w-64 bg-white rounded-md shadow-lg z-20 border border-gray-200 p-3">
          <h4 className="text-sm font-medium mb-2">Add Label</h4>
          <input
            type="text"
            value={newLabel}
            onChange={(e) => setNewLabel(e.target.value)}
            placeholder="Label text"
            className="w-full p-2 border border-gray-300 rounded text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
            autoFocus
          />
          <div className="mt-2">
            <p className="text-xs text-gray-600 mb-1">Color:</p>
            <div className="flex gap-1">
              {predefinedColors.map((color) => (
                <button
                  key={color}
                  onClick={() => setNewLabelColor(color)}
                  className={`w-6 h-6 rounded border-2 ${
                    newLabelColor === color ? 'border-gray-800' : 'border-gray-300'
                  }`}
                  style={{ backgroundColor: color }}
                />
              ))}
            </div>
          </div>
          <div className="flex justify-end gap-2 mt-3">
            <button
              onClick={() => {
                setShowLabelForm(false);
                setNewLabel('');
                setNewLabelColor('#3B82F6');
              }}
              className="text-xs text-gray-600 hover:text-gray-800"
            >
              Cancel
            </button>
            <button
              onClick={handleAddLabel}
              className="text-xs bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600"
            >
              Add
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Card;
