import React, { useState, useEffect } from 'react';
import Card from './Card';
import { getCards, createCard, deleteList } from '../services/api';

const ListExpanded = ({ list, onClose, onDelete }) => {
  const [cards, setCards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddCard, setShowAddCard] = useState(false);
  const [newCardTitle, setNewCardTitle] = useState('');
  const [newCardDescription, setNewCardDescription] = useState('');
  const [newCardStatus, setNewCardStatus] = useState('To Do');

  useEffect(() => {
    fetchCards();
  }, [list.id]);

  const fetchCards = async () => {
    try {
      setLoading(true);
      const data = await getCards(list.id);
      setCards(data);
    } catch (error) {
      console.error('Error fetching cards:', error);
    } finally {
      setLoading(false);
    }
  };

  const validateCardForm = () => {
    if (!newCardTitle.trim()) {
      alert('Card title is required');
      return false;
    }
    if (newCardTitle.length > 100) {
      alert('Card title must be less than 100 characters');
      return false;
    }
    if (newCardDescription && newCardDescription.length > 500) {
      alert('Card description must be less than 500 characters');
      return false;
    }
    return true;
  };

  const handleCreateCard = async (e) => {
    e.preventDefault();
    if (validateCardForm()) {
      try {
        const newCard = await createCard(list.id, newCardTitle.trim(), newCardDescription.trim(), newCardStatus);
        setCards([...cards, newCard]);
        setNewCardTitle('');
        setNewCardDescription('');
        setNewCardStatus('To Do');
        setShowAddCard(false);
      } catch (error) {
        console.error('Error creating card:', error);
        alert('Error creating card: ' + (error.response?.data?.error || 'Unknown error'));
      }
    }
  };

  const handleDeleteCard = (cardId) => {
    setCards(cards.filter(card => card.id !== cardId));
  };

  const handleCardUpdate = () => {
    // Refresh cards when a card is updated
    fetchCards();
  };

  const handleDeleteList = async () => {
    if (window.confirm('Are you sure you want to delete this list and all its cards?')) {
      try {
        await deleteList(list.id);
        onDelete(list.id);
        onClose();
      } catch (error) {
        console.error('Error deleting list:', error);
      }
    }
  };

  const getCardsByStatus = (status) => {
    return cards.filter(card => card.status === status);
  };

  if (loading) {
    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
        <div className="bg-white rounded-lg p-6">
          <div className="text-lg text-gray-600">Loading...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg p-6 w-full max-w-6xl max-h-[95vh] overflow-y-auto">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-bold text-gray-800">{list.title}</h2>
          <div className="flex space-x-2">
            <button
              onClick={handleDeleteList}
              className="text-red-600 hover:text-red-800 px-3 py-1 rounded text-sm hover:bg-red-50 transition-colors"
            >
              Delete List
            </button>
            <button
              onClick={onClose}
              className="text-gray-600 hover:text-gray-800 px-3 py-1 rounded text-sm hover:bg-gray-50 transition-colors"
            >
              Close
            </button>
          </div>
        </div>

        <div className="grid grid-cols-3 gap-6 h-[60vh] overflow-y-auto">
          {/* To Do Column */}
          <div className="bg-gray-50 rounded-lg p-4">
            <div className="flex justify-between items-center mb-4">
              <h3 className="font-semibold text-gray-800">To Do</h3>
              <span className="bg-gray-200 text-gray-700 px-2 py-1 rounded-full text-xs">
                {getCardsByStatus('To Do').length}
              </span>
            </div>
            <div className="space-y-3">
              {getCardsByStatus('To Do').map((card) => (
                <Card
                  key={card.id}
                  card={card}
                  onDelete={() => handleDeleteCard(card.id)}
                  onUpdate={handleCardUpdate}
                />
              ))}
            </div>
          </div>

          {/* In Progress Column */}
          <div className="bg-yellow-50 rounded-lg p-4">
            <div className="flex justify-between items-center mb-4">
              <h3 className="font-semibold text-gray-800">In Progress</h3>
              <span className="bg-yellow-200 text-yellow-700 px-2 py-1 rounded-full text-xs">
                {getCardsByStatus('In Progress').length}
              </span>
            </div>
            <div className="space-y-3">
              {getCardsByStatus('In Progress').map((card) => (
                <Card
                  key={card.id}
                  card={card}
                  onDelete={() => handleDeleteCard(card.id)}
                  onUpdate={handleCardUpdate}
                />
              ))}
            </div>
          </div>

          {/* Done Column */}
          <div className="bg-green-50 rounded-lg p-4">
            <div className="flex justify-between items-center mb-4">
              <h3 className="font-semibold text-gray-800">Done</h3>
              <span className="bg-green-200 text-green-700 px-2 py-1 rounded-full text-xs">
                {getCardsByStatus('Done').length}
              </span>
            </div>
            <div className="space-y-3">
              {getCardsByStatus('Done').map((card) => (
                <Card
                  key={card.id}
                  card={card}
                  onDelete={() => handleDeleteCard(card.id)}
                  onUpdate={handleCardUpdate}
                />
              ))}
            </div>
          </div>
        </div>

        {/* Add Card Form */}
        {showAddCard && (
          <div className="mt-6 p-4 bg-gray-100 rounded-lg">
            <form onSubmit={handleCreateCard} className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <input
                  type="text"
                  value={newCardTitle}
                  onChange={(e) => setNewCardTitle(e.target.value)}
                  placeholder="Enter card title..."
                  className="p-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                  autoFocus
                />
                <textarea
                  value={newCardDescription}
                  onChange={(e) => setNewCardDescription(e.target.value)}
                  placeholder="Enter description (optional)..."
                  className="p-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500 resize-none"
                  rows="1"
                />
                <select
                  value={newCardStatus}
                  onChange={(e) => setNewCardStatus(e.target.value)}
                  className="p-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="To Do">To Do</option>
                  <option value="In Progress">In Progress</option>
                  <option value="Done">Done</option>
                </select>
              </div>
              <div className="flex justify-end space-x-2">
                <button
                  type="submit"
                  className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors"
                >
                  Add Card
                </button>
                <button
                  type="button"
                  onClick={() => {
                    setShowAddCard(false);
                    setNewCardTitle('');
                    setNewCardDescription('');
                    setNewCardStatus('To Do');
                  }}
                  className="text-gray-600 px-4 py-2 rounded hover:text-gray-800 transition-colors"
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        )}

        {!showAddCard && (
          <div className="mt-4 text-center">
            <button
              onClick={() => setShowAddCard(true)}
              className="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600 transition-colors"
            >
              + Add New Card
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default ListExpanded;
