import React, { useState, useEffect } from 'react';
import { updateCard, deleteCard, getUsers } from '../services/api';

const Card = ({ card, onDelete, onUpdate }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [title, setTitle] = useState(card.title);
  const [description, setDescription] = useState(card.description || '');
  const [status, setStatus] = useState(card.status || 'To Do');
  const [assignedTo, setAssignedTo] = useState(card.assigned_to || null);
  const [showMenu, setShowMenu] = useState(false);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const usersData = await getUsers();
      setUsers(usersData);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const [editErrors, setEditErrors] = useState({});

  const validateCard = () => {
    const newErrors = {};
    
    if (!title.trim()) {
      newErrors.title = 'Card title is required';
    } else if (title.length > 100) {
      newErrors.title = 'Card title must be less than 100 characters';
    }
    
    if (description && description.length > 500) {
      newErrors.description = 'Card description must be less than 500 characters';
    }
    
    setEditErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSave = async () => {
    if (validateCard()) {
      try {
        const updatedCard = await updateCard(card.id, title.trim(), description.trim(), status, assignedTo);
        // Update the card object with new data
        card.title = title.trim();
        card.description = description.trim();
        card.status = status;
        card.assigned_to = assignedTo;
        setEditErrors({});
        setIsEditing(false);
        // Notify parent component to refresh
        if (onUpdate) {
          onUpdate();
        }
      } catch (error) {
        console.error('Error updating card:', error);
        setEditErrors({ general: 'Error updating card: ' + (error.response?.data?.error || 'Unknown error') });
      }
    }
  };

  const handleInputChange = (field, value) => {
    if (field === 'title') {
      setTitle(value);
    } else if (field === 'description') {
      setDescription(value);
    } else if (field === 'status') {
      setStatus(value);
    } else if (field === 'assignedTo') {
      setAssignedTo(value ? parseInt(value) : null);
    }
    
    if (editErrors[field]) {
      setEditErrors({ ...editErrors, [field]: '' });
    }
  };

  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this card?')) {
      try {
        await deleteCard(card.id);
        onDelete(card.id);
      } catch (error) {
        console.error('Error deleting card:', error);
      }
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSave();
    }
    if (e.key === 'Escape') {
      setTitle(card.title);
      setDescription(card.description || '');
      setStatus(card.status || 'To Do');
      setAssignedTo(card.assigned_to || null);
      setIsEditing(false);
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('vi-VN', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'To Do':
        return 'bg-gray-100 text-gray-800';
      case 'In Progress':
        return 'bg-yellow-100 text-yellow-800';
      case 'Done':
        return 'bg-green-100 text-green-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  if (isEditing) {
    return (
      <div className="bg-white rounded p-3 shadow-sm border border-gray-200">
        <textarea
          value={title}
          onChange={(e) => handleInputChange('title', e.target.value)}
          onKeyDown={handleKeyPress}
          className={`w-full p-2 border rounded focus:outline-none focus:ring-2 resize-none ${
            editErrors.title 
              ? 'border-red-500 focus:ring-red-500' 
              : 'border-gray-300 focus:ring-blue-500'
          }`}
          rows="2"
          autoFocus
        />
        {editErrors.title && (
          <p className="mt-1 text-sm text-red-600">{editErrors.title}</p>
        )}
        <textarea
          value={description}
          onChange={(e) => handleInputChange('description', e.target.value)}
          onKeyDown={handleKeyPress}
          placeholder="Add description..."
          className={`w-full p-2 mt-2 border rounded focus:outline-none focus:ring-2 resize-none ${
            editErrors.description 
              ? 'border-red-500 focus:ring-red-500' 
              : 'border-gray-300 focus:ring-blue-500'
          }`}
          rows="2"
        />
        {editErrors.description && (
          <p className="mt-1 text-sm text-red-600">{editErrors.description}</p>
        )}
        <select
          value={status}
          onChange={(e) => handleInputChange('status', e.target.value)}
          className="w-full p-2 mt-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
        >
          <option value="To Do">To Do</option>
          <option value="In Progress">In Progress</option>
          <option value="Done">Done</option>
        </select>
        <select
          value={assignedTo || ''}
          onChange={(e) => handleInputChange('assignedTo', e.target.value)}
          className="w-full p-2 mt-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
        >
          <option value="">No one assigned</option>
          {users.map((user) => (
            <option key={user.id} value={user.id}>
              {user.username}
            </option>
          ))}
        </select>
        {editErrors.general && (
          <p className="mt-2 text-sm text-red-600">{editErrors.general}</p>
        )}
        <div className="flex justify-end space-x-2 mt-2">
          <button
            onClick={handleSave}
            className="bg-blue-500 text-white px-3 py-1 rounded text-sm hover:bg-blue-600 transition-colors"
          >
            Save
          </button>
          <button
            onClick={() => {
              setTitle(card.title);
              setDescription(card.description || '');
              setStatus(card.status || 'To Do');
              setAssignedTo(card.assigned_to || null);
              setEditErrors({});
              setIsEditing(false);
            }}
            className="text-gray-600 px-3 py-1 rounded text-sm hover:text-gray-800 transition-colors"
          >
            Cancel
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white rounded p-3 shadow-sm border border-gray-200 hover:shadow-md transition-shadow cursor-pointer group relative">
      <div onClick={() => setIsEditing(true)}>
        <div className="flex justify-between items-start mb-2">
          <h4 className="font-medium text-gray-800 text-sm flex-1">{card.title}</h4>
          <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(card.status || 'To Do')}`}>
            {card.status || 'To Do'}
          </span>
        </div>
        {card.description && (
          <p className="text-gray-600 text-xs mb-2">{card.description}</p>
        )}
        <div className="flex justify-between items-center mt-2">
          <p className="text-gray-400 text-xs">
            Created: {formatDate(card.created_at)}
          </p>
          {card.assigned_to && (
            <span className="text-xs bg-blue-100 text-blue-800 px-2 py-1 rounded">
              {users.find(u => u.id === card.assigned_to)?.username || 'Unknown'}
            </span>
          )}
        </div>
      </div>
      
      <button
        onClick={(e) => {
          e.stopPropagation();
          setShowMenu(!showMenu);
        }}
        className="absolute top-2 right-2 text-gray-400 hover:text-gray-600 opacity-0 group-hover:opacity-100 transition-opacity"
      >
        ⋯
      </button>

      {showMenu && (
        <div className="absolute top-8 right-2 w-32 bg-white rounded-md shadow-lg z-10 border border-gray-200">
          <button
            onClick={(e) => {
              e.stopPropagation();
              setIsEditing(true);
              setShowMenu(false);
            }}
            className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-50"
          >
            Edit
          </button>
          <button
            onClick={(e) => {
              e.stopPropagation();
              handleDelete();
              setShowMenu(false);
            }}
            className="block w-full text-left px-4 py-2 text-sm text-red-600 hover:bg-gray-50"
          >
            Delete
          </button>
        </div>
      )}
    </div>
  );
};

export default Card;
